/**
 * Configures a Flux Standard Action creator injected with dispatch, getState and the libraries context.
 * @example <caption>Exports a Flux Standard Action creator that takes a handler injected with dispatch, getState, and the libraries context.
 * export const createSomeUserAction = configureDispatcherAction((dispatch, getState, context) => dispatcher.action.execute('create-some-user-action'))
 * @param  {function} handler: (dispatch, getState, context) => { ... }
 */
export const configureAction = handler => context => (dispatch, getState) => handler(dispatch, getState, context)

/**
 * Configures a Flux Standard Action creator injected with the libraries dispatcher and context.
 * @example <caption>Exports a Flux Standard Action creator that takes a handler injected with the libraries dispatcher and context.
 * export const createSomeUserAction = configureDispatcherAction((dispatcher, context) => dispatcher.action.execute('create-some-user-action'))
 * @param  {function} handler: (context, dispatcher) => { ... }
 */
//export const configureDispatcherAction = (handler, dispatcher) => context => (dispatch, getState) => handler(dispatcher)
export const configureDispatcherAction = context => dispatcher => handler => (dispatch, getState) => handler(dispatcher(dispatch, getState), context)


/** Allows the user of lib to define custom redux actions that will be injected with libraries context when action is dispatched */
export const actionDefinition = (actionName, actionContext) => ([ actionName, actionContext ])

/** Lib Constants */
export const LIB_NAME = 'redux-idle-monitor'

/** Redux state root level key */
export const ROOT_STATE_KEY = 'idle'

/** Redux Action Type Constants */
export const ACTION_PREFIX = 'IDLEMONITOR'
export const IDLEMONITOR_ACTIVITY = `${ACTION_PREFIX}_ACTIVITY`

import { assert } from 'chai'
import { validateOpts } from './validate'

import  { createLogger } from './log'


/**
 * @typedef {Object} LibOpts
 * @property {string} libName the name of the library.
 * @property {function(context: LibContext): AppValidator} createValidator
 */

 /**
  * @typedef {function(opts: AppOpts)} AppValidator
  */

/**
 * @typedef {Object} AppOpts
 * @property {string} appName the name of the application.
 */

/**
 * @typedef {Object} LibContext
 * @property {string} libName the name of the library.
 * @property {string} appName the name of the application.
 * @property {string} actionNames the names of the defined actions (ordered).
 */

/**
 * @typedef {LibAction[]} LibActions
 */

/**
 * @typedef {Array} LibAction
 */

/**
 * @typedef {Object} LibContext
 * @property {string[]} actionNames the names of all the actions (ordered).
 */

/**
 * @typedef CreateContext
 * @type {function(appOpts: AppOpts): AppContext }
 */



const noop = () => {}
const cleanActionName = name => name.toUpperCase().replace(/-+\s+/, '_')
const configureActionName = libName => appName => actionName => `${cleanActionName(libName)}_${cleanActionName(appName)}_${cleanActionName(actionName)}`

/** Validates library creators options */
const validateLibOpts = libOptsRaw => {
  assert.ok(libOptsRaw, 'libOpts definition is required')
  const { libName, libActions, validateContext, configureAppContext, configureInitialState } = libOptsRaw
  assert(typeof libName === 'string', 'libName must be a string')
  assert(libName.length > 0, 'libName must not be empty')

  assert.ok(libActions, 'libActions must exist')
  assert(Array.isArray(libActions), 'libActions must be an array')
  assert(libActions.every(x => Array.isArray(x)), 'libActions must be an array of an array')
  assert(libActions.every(x => x.length === 2), 'every item in libActions must have length 2')
  assert(libActions.every(x => typeof x[0] === 'string'), 'every item in libActions must have first ordinal type string action name')
  assert(libActions.every(x => typeof x[1] === 'object'), 'every item in libActions must have second ordinal type object actionContext')

  assert.ok(validateContext, 'validateContext must exist')
  assert(typeof validateContext === 'function', 'validateContext must be a function')

  assert.ok(configureAppContext, 'configureAppContext must exist')
  assert(typeof configureAppContext === 'function', 'configureAppContext must be a function')

  assert.ok(configureInitialState, 'configureInitialState must exist')
  assert(typeof configureInitialState === 'function', 'configureInitialState must be a function')
}

/** Validates library consumers options */
const validateAppOpts = appOptsRaw => {
  assert.ok(appOptsRaw, 'appOpts are required')
  const { appName } = appOptsRaw

  assert(typeof appName === 'string', 'appName opt must be a string')
  assert(appName.length > 0, 'appName opt must not be empty')
}

const validateAppContext = context => {
}



const isDev = process.env.NODE_ENV !== 'production'

const normalizeLibOpts = libOptsRaw => {
  if(isDev) validateLibOpts(libOptsRaw)
  const { libName, libActions, validateContext, configureAppContext, configureInitialState } = libOptsRaw

  const libActionMap = new Map(libActions)
  const libActionNames = libActions.map(x => x[0])
  const validateAgainstLibOpts = appOptsRaw => {
    assert(appOptsRaw.appActions.every(x => !libActionNames.includes(x[0])), 'action names must be unique across lib and app')
  }
  return  { libName
          , libActions
          , libActionMap
          , libActionNames
          , validateContext
          , configureAppContext
          , configureInitialState
          , validateAgainstLibOpts
          }

}
const normalizeAppOpts = appOptsRaw => {
  if(isDev) validateAppOpts(appOptsRaw)
  const { appName, appActions } = appOptsRaw
  return  { ...appOptsRaw
          , appActionMap: new Map(appActions)
          , appActionNames: appActions.map(x => x[0])
          }
}
/*
import configureContext from 'redux-addons/context'
const context = configureContext(libOpts)(appOpts)
const {  } = context
 */
export default function configureContext(libOptsRaw) {
  const libOpts = normalizeLibOpts(libOptsRaw)
  const { libName, libActions, libActionMap, libActionNames, validateContext, configureAppContext, configureInitialState, validateAgainstLibOpts } = libOpts
  return appOptsRaw => {
    if(isDev) validateAgainstLibOpts(appOptsRaw)
    const appOpts = normalizeAppOpts(appOptsRaw)
    const { appName, appActions, appActionMap, appActionNames, level } = appOpts

    const actions = [...libActions, ...appActions]
    const actionMap = new Map(actions)
    const actionNames = actions.map(x => x[0])

    const createActionType =  actionName => `${cleanActionName(libName)}_${cleanActionName(appName)}_${cleanActionName(actionName)}`
    const typedLibActions = libActions.map(x => [createActionType(x[0]), x[1]])
    const typedAppActions = appActions.map(x => [createActionType(x[0]), x[1]])
    const libActionTypes = typedLibActions.map(x => x[0])
    const appActionTypes = typedAppActions.map(x => x[0])
    const typedActions = [...typedLibActions, ...typedAppActions]
    const typedActionMap = new Map(typedActions)
    const actionTypes = typedActions.map(x => x[0])

    const getActionType = actionName => actionTypes[actionNames.indexOf(actionName)]

    const getActionContextByName = actionName => actionMap.get(actionName)
    const getActionContextByType = actionType => typedActionMap.get(actionType)
    const getLibActionContextByOrdinal = ordinal => libActions[ordinal][1]
    const getAppActionContextByOrdinal = ordinal => appActions[ordinal][1]

    const libContext =  { log: createLogger({ libName, level })
                        , libName
                        , libActions
                        , libActionMap
                        , libActionNames
                        , appName
                        , appActions
                        , appActionMap
                        , appActionNames
                        , actions
                        , actionMap
                        , actionNames
                        , createActionType
                        , typedLibActions
                        , typedAppActions
                        , libActionTypes
                        , appActionTypes
                        , typedActions
                        , typedActionMap
                        , actionTypes
                        , getActionType
                        , getActionContextByName
                        , getActionContextByType
                        , getLibActionContextByOrdinal
                        , getAppActionContextByOrdinal
                        }


    const appContext = configureAppContext(libContext)(appOpts)
    if(process.env.NODE_ENV !== 'production') {
      validateAppContext(appContext)
      validateContext(libContext, appContext)
    }

    return Object.assign( appContext, libContext, { get initialState() { return configureInitialState(libContext)(appContext) }
                                                  })
  }
}

import { defineAction } from './actions'
const getTimeStamp = () => {
  return new Date().toTimeString()
}
const defaultActiveAction = () => defineAction('ACTIVE', (dispatch, getState, { log }) => log.info(`USER ACTIVE AT ${getTimeStamp()}`), 0)
const defaultInactiveAction = () => defineAction('INACTIVE', (dispatch, getState, { log }) => log.info(`USER INACTIVE AT ${getTimeStamp()}`), 5000)
const defaultIdleAction = () => defineAction('IDLE', (dispatch, getState, { log }) => log.info(`USER IDLE AT ${getTimeStamp()}`), 5000)
const defaultExpiredAction = () => defineAction('EXPIRED', (dispatch, getState, { log }) => {
  log.info(`USER EXPIRED AT ${getTimeStamp()}`)
}, 15000)

export const getActions = () => [ defaultActiveAction()
                                , defaultInactiveAction()
                                , defaultIdleAction()
                                , defaultExpiredAction()
                                ]

export const getActiveEvents = () => [ 'mousemove', 'keydown', 'wheel', 'DOMMouseScroll', 'mouseWheel', 'mousedown', 'touchstart', 'touchmove', 'MSPointerDown', 'MSPointerMove' ]

export const getUseFastState = () => true
export const getUseLocalState = () => true
export const getUseWebRTCState = () => true
export const getUseWebSocketsState = () => true

export const getThresholds = ({ mouse = 10, elapsedMS = 1000 } = { mouse: 10, elapsedMS: 1000 }) => ({ mouse, elapsedMS })

export const getLevel = () => 'trace'

import { assert } from 'chai'
import { createAction } from 'redux-actions'
import  { ROOT_STATE_KEY
        , IDLEMONITOR_ACTIVITY
        } from './constants'
import { createActions, start } from 'vendor/redux-idle-monitor/actions'

export default function configureDispatcher(context) {
  return (dispatch, getState) => {
    const stores = createStoresDispatcher(context)(dispatch, getState)
    const timeout = createTimeoutDispatcher(context, { stores })(dispatch, getState)
    const detection = createDetectionDispatcher(context, { stores })(dispatch, getState)
    const action = createActionDispatcher(context, { timeout, stores, detection })(dispatch, getState)
    return { stores, timeout, detection, action }
  }
}

const createStoresDispatcher = context => (dispatch, getState) => {
  const { log, getAction, getTimeoutMS, useFastState, useLocalState, initialLastEvent } = context

  const fastStateKeys = ['lastActive', 'lastEvent', 'timeoutID', 'isDetectionRunning']
  const localStateKeys = ['lastActive']
  const reduxStateKeys = ['actionName', 'isIdle', 'isPaused', 'lastActive', 'lastEvent', 'timeoutID', 'isDetectionRunning']

  const _shouldSetState = (stateKeys, newState) => Object.keys(newState).some(x => fastStateKeys.includes(x) && typeof newState[x] !== 'undefined')

  const _shouldSetFastState = newState => useFastState && _shouldSetState(fastStateKeys, newState)
  const _shouldSetLocalState = newState => useLocalState && _shouldSetState(localStateKeys, newState)
  const _shouldSetReduxState = newState => _shouldSetState(reduxStateKeys, newState)

  const _filterState = (newState, stateKeys) => {
    return Object.keys(newState).reduce((state, key) => {
      if(stateKeys.includes(key))
        state[key] = newState[key]
      return state
    }, {})
  }

  const createFastState = ( { lastEvent = initialLastEvent
                            , timeoutID } = {}) => ({ lastActive: +new Date()
                                                    , lastEvent
                                                    , timeoutID
                                                    , isDetectionRunning: false
                                                    })
  let fastState = useFastState ? createFastState() : noop()
  const setFastState = newState => {
    fastState = Object.assign({}, fastState, _filterState(newState, fastStateKeys), { lastActive: +new Date() })
    if(process.env.NODE_ENV !== 'production')
      log.debug({ fastState }, 'fastState set')
  }

  const createLocalState = ({} = {}) => ({ lastActive: +new Date() })
  if(useLocalState)
    localStorage[IDLEMONITOR_ACTIVITY] = createLocalState()
  const getLocalState = () => {
    return localStateKeys.reduce((state, key) => {
      state[key] = localStorage[`${IDLEMONITOR_ACTIVITY}_${key}`]
      return state
    }, {})
  }
  const setLocalState = (newState) => {
    localStorage[`${IDLEMONITOR_ACTIVITY}_lastActive`] = +new Date()
    Object.keys(newState).filter(key => localStateKeys.includes(key)).forEach((key) => localStorage[`${IDLEMONITOR_ACTIVITY}_${key}`] = newState[key])
    //localStorage[IDLEMONITOR_ACTIVITY] = Object.assign({}, localStorage[IDLEMONITOR_ACTIVITY], _filterState(newState, localStateKeys), { lastActive: +new Date() })
    if(process.env.NODE_ENV !== 'production')
      log.trace({ localState: getLocalState() }, 'localState set')
  }

  /** GETS THIS LIBS SLICE OF TOP LEVEL STATE FROM REDUX (supports immutable) */
  const getLibState = () => {
    const state = getState()
    return state.isMap && state.isMap() ? state.get(ROOT_STATE_KEY) : state[ROOT_STATE_KEY]
  }

  /** ABSTRACTS ACCESS TO STATE VIA GETTERS */
  const getLibStateAccessor = libState => {
              /** The current state name */
    return  { get actionName() { return libState.actionName }
              /** Is in idle state (no more states to progress to) */
            , get isIdle() { return libState.isIdle }
              /** State can be paused manually or via action dispatch or returning null/undefined from timeoutMS function */
            , get isPaused() { return libState.isPaused }
              /** The epoch MS that the user was last active */
            , get lastActive() { return useFastState ? fastState.lastActive : libState.lastActive }
              /** Event information captured on the last recorded user action */
            , get lastEvent() { return useFastState ? fastState.lastEvent : libState.lastEvent }
              /** The timeoutID for the current scheduled next event if it exists */
            , get timeoutID() { return useFastState ? fastState.timeoutID : libState.timeoutID }
            }
  }

  const getReduxState = () => {
    const state = getLibStateAccessor(getLibState())

    return  { ...state
            , get next() {
                const events = context.actionNames
                const nextIndex = events.indexOf(state.actionName) + 1
                return events[nextIndex] /** MAY BE UNDEFINED */
              }
            , get action() { return getAction(state.actionName) }
            , get timeoutMS() { return getTimeoutMS(state.actionName) }
            , get remainingMS() {
                if(state.isIdle)
                  return 0
                const remainingMS = getTimeoutMS(state.actionName) - (+new Date() - state.lastActive)
                return remainingMS > 0 ? remainingMS : 0
              }
            }
  }


  return  { get redux() { return getReduxState() }
            /** Without some way to track fast state (mousemove events), redux gets spammed with actions */
          , get fast() { return useFastState ? fastState : getReduxState() }
            /** Things that need to be synced across tabs (lastActive, lastEvent) */
          , get local() { return {} }
            /** All state update actions flow through this, has ability to bypass redux for fast state operations or dispatch to it */
          , setState: (actionName, newState) => {
              if(_shouldSetLocalState)
                setLocalState(newState)
              if(_shouldSetFastState(newState)) {
                setFastState(newState)
                if(!_shouldSetReduxState(newState))
                  return log.debug('bypassing redux state update')
              }
              log.debug({ newState }, 'updating redux state')
              return dispatch(createAction(actionName)(newState))
            }
          }
}

const createTimeoutDispatcher = (context, { stores }) => (dispatch, getState) => {
  const { getFastState, getTimeoutMS } = context
  return  { clear: () => clearTimeout(stores.fast.timeoutID)
          , timeoutMS: actionName => {
              let result = getTimeoutMS(actionName)
              return typeof result === 'function' ? result(dispatch, getState, _getChildContext(context)) : result
            }
          }
}

const createDetectionDispatcher = (context, { stores }) => (dispatch, getState) => {
  const { log, activeEvents, initialActionName, initialAction, thresholds } = context
  const { setState } = stores


  /** Detects whether the activity should trigger a redux update */
  const _shouldActivityUpdate = ({ type, pageX, pageY }) => {
    if(type !== 'mousemove') return true

    const { lastActive, lastEvent: { x, y } } = stores.fast
    if (typeof pageX === 'undefined' || typeof pageY === 'undefined')
      return false
    if(Math.abs(pageX - x) < thresholds.mouse && Math.abs(pageY - y) < thresholds.mouse)
      return false

    // SKIP UPDATE IF ITS UNDER THE THRESHOLD MS FROM THE LAST UPDATE
    let elapsedMS = (+new Date()) - lastActive
    if (elapsedMS < thresholds.elapsedMS)
      return false
    if(process.env.NODE_ENV !== 'production')
      log.trace(`_shouldActivityUpdate: elapsed vs threshold => E[${elapsedMS}] >= T[${thresholds.elapsedMS}], lastActive => ${lastActive}`)
    return true
  }

  const _shouldRestart = () => stores.redux.actionName !== initialActionName

  /** One of the event listeners triggered an activity occurrence event. This gets spammed */
  const onActivity = e => {
    if (!_shouldActivityUpdate(e))
      return
    if(_shouldRestart())
      return dispatch(context.actions.start())
    /** THIS WILL BE ROUTED TO FAST OR LOCAL STATE IF ENABLED */
    setState(IDLEMONITOR_ACTIVITY, { lastActive: +new Date(), lastEvent: { x: e.pageX, y: e.pageY } })
  }

  return  { get isRunning() { return stores.fast.isDetectionRunning }
          , start: () => {
              if(process.env.NODE_ENV !== 'production')
                assert(stores.fast.isDetectionRunning === false, 'activity detection is already running')
              activeEvents.forEach(x => document.addEventListener(x, onActivity))
              log.debug('activity detection started...')
              setState(IDLEMONITOR_ACTIVITY, { isDetectionRunning: true })
            }
          , stop: () => {
              if(process.env.NODE_ENV !== 'production')
                assert(stores.fast.isDetectionRunning === true, 'activity detection is not running')
              activeEvents.forEach(x => document.removeEventListener(x, onActivity))
              log.debug('activity detection stopped')
              setState(IDLEMONITOR_ACTIVITY, { isDetectionRunning: false })
            }
          }

}


const createActionDispatcher = (context, { timeout, stores, detection }) => (dispatch, getState) => {
  const { log, getTimeoutMS, getAction, getNextActionName, useFastState, setFastState } = context
  const { setState } = stores
  const _isPauseTriggered = timeoutMS => timeoutMS === null || timeoutMS === false || typeof timeoutMS === 'undefined'

  /** Responsible for clearing old timeout and scheduling new one or immediately executing, returns new timeoutID or undefined */
  const schedule = actionName => {
    timeout.clear()
    const timeoutMS = timeout.timeoutMS(actionName)
    log.debug({ actionName, timeoutMS }, 'schedule')
    const args = { actionName, isPaused: _isPauseTriggered(timeoutMS) }
    if(timeoutMS > 0)
      return setTimeout(() => execute(args), timeoutMS)
    execute(args)
  }

  /** Responsible for executing an action */
  const execute = ({ actionName, isPaused }) => {
    const nextActionName = getNextActionName(actionName)
    const wasPaused = stores.redux.isPaused

    /** TODO: CHECK LOCAL STATE HERE AND IF ITS BEEN ACTIVE, POSTPONE THE ACTION ABOUT TO BE EXECUTED */

    /** SCHEDULE THE NEXT ACTION IF IT EXISTS */
    let timeoutID = nextActionName && !isPaused ? schedule(nextActionName) : null

    if(isPaused && !wasPaused) {
      log.info('pausing activity detection')
      detection.stop()
    }
    if(!isPaused && wasPaused) {
      log.info('unpausing activity detection')
      detection.start()
    }

    /** UPDATE THE STATE OF THE APP */
    setState(actionName,  { actionName: actionName
                          , isIdle: typeof nextActionName === 'undefined'
                          , isPaused
                          , timeoutID
                          })

    /** EXECUTE THE USER DEFINED ACTION WITH REDUX THUNK ARGS + CONTEXT (LOG, START/STOP/RESET DISPATCHABLE ACTIONS) */
    getAction(actionName)(dispatch, getState, _getChildContext(context))
  }
  return { schedule, execute }
}

const _getChildContext = context => ({ ...context, actions: context.childActions })

import createContext from './context'

import { createReducer } from './reducer'
import { createActions, defineAction } from './actions'
import { createMiddleware } from './middleware'

export { defineAction }

export default function configure(opts) {
  const context = createContext(opts)
  return  { reducer: createReducer(context)
          , actions: createActions(context)
          , middleware: createMiddleware(context)
          }
}

const noop = () => {}

export const createLogger = ({ libName, level }) => {
  const _formatMessage = ({ level, message, obj }) => {
    if(!message && typeof obj === 'string') {
      message = obj
      obj = noop()
    }
    return _formatLog(obj ? `${level}: '${message}' => ${JSON.stringify(obj)}`  : `${level}: '${message}'`)
  }

  const _formatLog = message =>`${libName} | ${message}`

  return process.env.NODE_ENV !== 'production' ? (
    { trace: (obj, message) => level === 'trace' ? console.trace(_formatMessage({ level: 'trace', message, obj })): noop()
    , debug: (obj, message) => ['trace','debug'].includes(level) ? console.log(_formatMessage({ level: 'debug', message, obj })) : noop()
    , info: (obj, message) => ['trace','debug','info'].includes(level) ? console.info(_formatMessage({ level: 'info', message, obj })) : noop()
    , warn: (obj, message) => ['trace','debug','info','warn'].includes(level) ? console.warn(_formatMessage({ level: 'warn', message, obj })) : noop()
    , error: (obj, message) => ['trace','debug','info','warn','error'].includes(level) ? console.error(_formatMessage({ level: 'error', message, obj })) : noop()
    }
  ) : ({ trace: noop, debug: noop, info: noop, warn: noop, error: noop })
}

import middleware, { applyLogicalMiddleware } from './middleware/src/lib'
export { applyLogicalMiddleware }
export default middleware

import createContext from './context'
import  { IDLEMONITOR_ACTIVITY } from './constants'

/** When context has already been created, it can be shared to middleware component. */
export const createReducer = context => {
  const { log, initialState, actionNames, useFastState, useLocalState, useWebRTCState, useWebSocketsState } = context
  return (state = initialState, action = {}) => {
    if(!actionNames.includes(action.type))
      return state

    const { type, payload } = action
    if(type === IDLEMONITOR_ACTIVITY) {
      if(useFastState)
        return state
      const { lastActive, lastEvent, timeoutID } = payload
      return Object.assign({}, state, { lastActive, lastEvent, timeoutID })
    }

    const { actionName, isIdle, isPaused, lastActive, lastEvent, timeoutID } = payload
    return Object.assign({}, state, { actionName, isIdle, isPaused, lastActive, lastEvent, timeoutID })
  }
}

/** Creates reducer from opts including validation in development */
export default function configureReducer (opts) { return createReducer(createContext(opts)) }
